<link rel="stylesheet" type="text/css" href="//cdn.jsdelivr.net/gh/kenwheeler/slick@1.8.1/slick/slick.css"/>
<script type="text/javascript" src="//cdn.jsdelivr.net/gh/kenwheeler/slick@1.8.1/slick/slick.min.js"></script>
<script>
var item_length = $('.timeline > div').length - 1,
	wrongAnswers = 0;

$('.timeline').slick({
    infinite: false,
    dots: true,
}).on('afterChange', function(event, slick, currentSlide, nextSlide){
	if(currentSlide == item_length) {
		$('.timeline-quiz').removeClass('hide');
	}
});
    var rightAnswers = 0,
        currentQuestion = 1;
$( ".quiz-options a" ).click(function() {
    var answer = $(this).data('answer');
	$('.js-whoops').addClass('hide');
    if(answer === true) {
		$(this).closest('.quiz-question').find('.js-error').addClass('hide');
        rightAnswers++;
        $(this).addClass('selected-right');
        $('.jsQuestion'+currentQuestion).addClass('disabled');
        currentQuestion++;
		wrongAnswers = 0;
        if(currentQuestion === 5) {
            $('.jsCorrectAnswers').text(rightAnswers);
            $('.jsNextStep').removeClass('hide');
            $('.quiz-summary').removeClass('hide');
            oBadges.stepCompleted();
        } else {
            $('.jsQuestionNumber').text(currentQuestion);
            $('.jsQuestion'+currentQuestion).removeClass('hide');
        }
    } else {
        $(this).addClass('selected-wrong');
		$(this).closest('.quiz-question').find('.js-error').removeClass('hide');
		wrongAnswers++;
		if(wrongAnswers == 2) {
			$('.js-error').addClass('hide');
			$('.quiz-options .selected-wrong').removeClass('selected-wrong');
			$('.quiz-options .selected-right').removeClass('selected-right');
			$('.jsQuestion2, .jsQuestion3, .jsQuestion4').removeClass('disabled').addClass('hide');
			$('.jsQuestion1').removeClass('disabled');
			$('.js-whoops').removeClass('hide');
			currentQuestion = 1;
			wrongAnswers = 0;
			rightAnswers = 0;

		}
        return false;
    }



});
</script>


