<section class="jsMainChat overlay overlay__purple--bg">
    <div class="chatbot__container" data-aos="slide-up" data-aos-duration="720">
        <div class="chatbot__top">
            <button id="chatbot__button--close-chat" class="hide">
                Close Chat
            </button>
        </div>

        <div class="chatbot__middle"></div>

        <div class="chatbot__bottom">
            <textarea id="user-input" placeholder="Type here..."></textarea>
            <button id="send">
                <img
                    src="/badge-assets/chatbots/_global/svg/icon--send.svg"
                    alt="Send message"
                />
            </button>
        </div>
    </div>
</section>
