<script>
var badgeTitleWidth = 920,
    viewportWidth = $(window).width(),
    transformScale;
    
    if(badgeTitleWidth > (viewportWidth - 100)) {
        transformScale = (viewportWidth-100) / badgeTitleWidth;
        $('.badge-title').css('transform','scale('+transformScale+')').css('width','300%').css('margin-left','-100%');
    } else {
    $('.badge-title').css('transform','scale(1)');
    }
    
    $( window ).resize(function() {
        var badgeTitleWidth = 920,
    viewportWidth = $(window).width(),
    transformScale;
    
    if(badgeTitleWidth > (viewportWidth - 100)) {
        transformScale = (viewportWidth-100) / badgeTitleWidth;
        $('.badge-title').css('transform','scale('+transformScale+')').css('width','300%').css('margin-left','-100%');
    } else {
    $('.badge-title').css('transform','scale(1)');
    }
    });

</script>

<script>

var rightAnswers = 0,
    currentQuestion = 1;
$( ".software-quiz-options a" ).click(function() {
    var answer = $(this).data('answer');
    $('.jsQuestion'+currentQuestion).find('.software-quiz-answer--wrong').addClass('hide');
    $('.jsQuestion'+currentQuestion+' .selected').removeClass('selected');
    if(answer === true) {
        rightAnswers++;
        $(this).addClass('selected');
        $('.jsQuestion'+currentQuestion).addClass('disabled').find('.software-quiz-answer--right').removeClass('hide');
        currentQuestion++;
        if(currentQuestion === 5) {
            $('.jsNextStep').removeClass('hide');
            $('.quiz-summary').removeClass('hide');
            oBadges.stepCompleted();
        } else {
            $('.jsQuestion'+currentQuestion).removeClass('hide');
        }
    } else {
        $(this).addClass('selected');
        $('.jsQuestion'+currentQuestion).find('.software-quiz-answer--wrong').removeClass('hide');
        return false;
    }



});
</script>

